'use strict';

var ResFetch = require('../lib/resfetch');

module.exports = function(grunt) {
    grunt.registerMultiTask('resfetch', 'fetch html relate resource.', function() {
        var distPath = this.options().distPath;
        this.files.forEach(function(f) {
            f.src.forEach(function(filePath){
                new ResFetch({'filePaths':[filePath] , 'distPath':distPath});
            });
        });
    });
}